var cardCountOfLevel=[1,4,10,20,50,100];function getLevelByCount(b){for(var a=0,c=cardCountOfLevel.length-1;0<=c;c--)if(b>=cardCountOfLevel[c]){a=c;break}return a}function getRequiredCardCount(b){if(b>cardCountOfLevel[cardCountOfLevel.length-1])return 0;for(var a=cardCountOfLevel.length-1;0<=a;a--)if(b>=cardCountOfLevel[a])return cardCountOfLevel[a+1]-b;return cardCountOfLevel[0]-b};
